mapper.select:
SELECT recipe_no, created_t::date AS date, COALESCE(SUM(CASE WHEN lane1 != 0 THEN 1 ELSE 0 END + CASE WHEN lane2 != 0 THEN 1 ELSE 0 END + CASE WHEN lane3 != 0 THEN 1 ELSE 0 END + CASE WHEN lane4 != 0 THEN 1 ELSE 0 END + CASE WHEN lane5 != 0 THEN 1 ELSE 0 END + CASE WHEN lane6 != 0 THEN 1 ELSE 0 END), 0) AS ng_count FROM history
recipe_no = #{id}
AND created_t::date BETWEEN #{filter.startDate}::date + INTERVAL '1 day' AND #{filter.endDate}::date + INTERVAL '1 day'
GROUP BY recipe_no, created_t::date ORDER BY date;

SELECT recipe_no, created_t::date AS date, COALESCE(SUM(CASE WHEN lane1 != 0 THEN 1 ELSE 0 END + CASE WHEN lane2 != 0 THEN 1 ELSE 0 END + CASE WHEN lane3 != 0 THEN 1 ELSE 0 END + CASE WHEN lane4 != 0 THEN 1 ELSE 0 END + CASE WHEN lane5 != 0 THEN 1 ELSE 0 END + CASE WHEN lane6 != 0 THEN 1 ELSE 0 END), 0) AS ng_count FROM history
recipe_no = #{id}
AND created_t::date BETWEEN #{filter.startDate}::date + INTERVAL '1 day' AND #{filter.endDate}::date + INTERVAL '1 day'
GROUP BY recipe_no, created_t::date ORDER BY date;

INSERT INTO history(recipe_no, item_no, lane1, lane2, lane3, lane4, lane5, lane6, created_t)
VALUES (#{history.recipeNo}, #{history.itemNo}, #{history.lane1}, #{history.lane2}, #{history.lane3}, #{history.lane4}, #{history.lane5}, #{history.lane6}, #{history.createdT})
    returning *;
